SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_consistir_letra_unica_pf ( nm_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := 'N';
ds_lista_nome_w		varchar(4000);
pos_nome_w		bigint;
nm_pessoa_w		varchar(255);


BEGIN

ds_lista_nome_w	:= replace(nm_pessoa_fisica_p,'.',' ');
ds_lista_nome_w	:= ds_lista_nome_w || ' ';

while(ds_lista_nome_w IS NOT NULL AND ds_lista_nome_w::text <> '') and (ds_retorno_w = 'N') loop

	select	position(' ' in ds_lista_nome_w)
	into STRICT	pos_nome_w
	;

	if (pos_nome_w > 0) then
		nm_pessoa_w	:= substr(ds_lista_nome_w,1,pos_nome_w - 1);
		nm_pessoa_w	:= upper(nm_pessoa_w);

		ds_lista_nome_w	:= substr(ds_lista_nome_w,pos_nome_w + 1,length(ds_lista_nome_w));
		if (length(nm_pessoa_w) = 1) and (upper(nm_pessoa_w) not in ('E','Y','I','O','U')) then
			ds_retorno_w	:= 'S';
		end if;
	end if;
end loop;

--aaschlote 22/05/2012 OS - 444336
if (ds_retorno_w = 'N') then
	ds_retorno_w	:= pls_obter_se_possui_numero(nm_pessoa_fisica_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
