SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_cons_sexo_carencia_prop ( nr_seq_benef_prop_p bigint, nr_seq_tipo_carencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(10);
ie_sexo_benef_w		varchar(10);
ie_sexo_carencia_w	varchar(10);


BEGIN
ie_retorno_w	:= 'N';

/* Lepinski - OS 300121 - aaschlote */

if (nr_seq_tipo_carencia_p IS NOT NULL AND nr_seq_tipo_carencia_p::text <> '') then
	select	b.ie_sexo
	into STRICT	ie_sexo_benef_w
	from	pessoa_fisica			b,
		pls_proposta_beneficiario	a
	where	a.cd_beneficiario	= b.cd_pessoa_fisica
	and	a.nr_sequencia		= nr_seq_benef_prop_p;

	select	coalesce(ie_sexo,'A')
	into STRICT	ie_sexo_carencia_w
	from	pls_tipo_carencia
	where	nr_sequencia	= nr_seq_tipo_carencia_p;

	if (ie_sexo_benef_w	= 'I') then
		ie_retorno_w	:= 'S';
	elsif (coalesce(ie_sexo_benef_w::text, '') = '') then
		ie_retorno_w	:= 'S';
	elsif (ie_sexo_benef_w	= ie_sexo_carencia_w) then
		ie_retorno_w	:= 'S';
	elsif (ie_sexo_carencia_w	= 'A') then
		ie_retorno_w	:= 'S';
	end if;
else
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
