CREATE OR REPLACE FUNCTION pls_conta_obter_se_auditor_lib ( nr_seq_analise_p bigint, nr_seq_ocorrencia_p bigint, ie_tipo_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

				
/*	ie_tipo_p
	G - Glosa
	O - Ocorrencia
*/
ds_retorno_w			varchar(1)	:= 'S';
nr_seq_ocorrencia_w		bigint;
nr_nivel_lib_oc_w		bigint;
nr_seq_grupo_usuario_w		bigint;
nr_nivel_lib_usuario_w		bigint;
qt_reg_usuario_grupo_w		bigint;
dt_liberacao_w			timestamp;


BEGIN

if (ie_tipo_p	= 'O') then
	select	nr_seq_ocorrencia,
		nr_nivel_liberacao,
		dt_liberacao
	into STRICT	nr_seq_ocorrencia_w,
		nr_nivel_lib_oc_w,
		dt_liberacao_w
	from	pls_ocorrencia_benef
	where	nr_sequencia	= nr_seq_ocorrencia_p;
	
	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ds_retorno_w	:= 'N';
	else
		nr_seq_grupo_usuario_w	:= pls_cta_obter_grupo_analise(nr_seq_analise_p, nm_usuario_p, 'S');
		
		/* Verificar a liberacao atraves do nivel de liberacao */

		if (coalesce(nr_nivel_lib_oc_w,0) > 0) then
			nr_nivel_lib_usuario_w	:= pls_cta_nivel_grupo_analise(nr_seq_grupo_usuario_w, nm_usuario_p);
			
			if (nr_nivel_lib_usuario_w	< nr_nivel_lib_oc_w) then
				ds_retorno_w	:= 'N';
			end if;
		else
			select	count(*)
			into STRICT	qt_reg_usuario_grupo_w
			from	pls_ocorrencia_grupo
			where	nr_seq_ocorrencia	= nr_seq_ocorrencia_w
			and	nr_seq_grupo		= nr_seq_grupo_usuario_w
			and	ie_situacao		= 'A';
			
			if (qt_reg_usuario_grupo_w	= 0) then
				ds_retorno_w	:= 'N';
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
