CREATE OR REPLACE FUNCTION pls_converte_codigo_tuss_mat ( cd_material_tuss_p bigint, cd_versao_tiss_p text default '', dt_ref_material_p timestamp default clock_timestamp()) RETURNS PLS_MATERIAL.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_seq_material_w		pls_material.nr_Sequencia%type;
cd_versao_tiss_w		varchar(30);

c01 CURSOR(cd_material_tuss_p	tuss_material_item.cd_material_tuss%type)FOR
	 SELECT CASE WHEN a.cd_material_ops_number=b.cd_material_tuss THEN 1  ELSE 2 END  ie_ordem,
		a.nr_sequencia nr_sequencia
	 from  	pls_material a,
		tuss_material_item b
	 where  b.nr_sequencia  = a.nr_seq_tuss_mat_item
	 and 	b.cd_material_tuss = cd_material_tuss_p
	 order by 1;
BEGIN

if (cd_versao_tiss_p IS NOT NULL AND cd_versao_tiss_p::text <> '') then
	for	r_C01_w in c01(cd_material_tuss_p) loop

		 nr_seq_material_w := pls_obter_mat_tiss_vigente( nr_seq_material_w, dt_ref_material_p, r_C01_w.nr_sequencia, 'S', 'S', cd_versao_tiss_p);

	end loop;
end if;

if (coalesce(nr_seq_material_w::text, '') = '') then

	for	r_C01_w in c01(cd_material_tuss_p) loop

		 nr_seq_material_w := pls_obter_mat_tiss_vigente( nr_seq_material_w, dt_ref_material_p, r_C01_w.nr_sequencia, 'S', 'N', cd_versao_tiss_p);

	end loop;

end if;

return nr_seq_material_w;

end;
