CREATE OR REPLACE FUNCTION pls_converte_valor_qt_a550 ( nr_seq_contestacao_p ptu_camara_contestacao.nr_sequencia%type, vl_conversao_p bigint) RETURNS bigint AS $body$
DECLARE

 
ie_tipo_arquivo_w	ptu_camara_contestacao.ie_tipo_arquivo%type;
ie_envio_recebimento_w	pls_lote_contestacao.ie_envio_recebimento%type;
vl_convertido_w		double precision;


BEGIN 
 
select	max(a.ie_tipo_arquivo), 
	max(b.ie_envio_recebimento) 
into STRICT	ie_tipo_arquivo_w, 
	ie_envio_recebimento_w 
from	ptu_camara_contestacao	a, 
	pls_lote_contestacao	b 
where	b.nr_sequencia	= a.nr_seq_lote_contest 
and	a.nr_sequencia	= nr_seq_contestacao_p;
 
if (ie_tipo_arquivo_w = 1) and (ie_envio_recebimento_w = 'E') then -- pagamento 
	vl_convertido_w	:= vl_conversao_p;
else 
	vl_convertido_w := (vl_conversao_p/10000);
end if;
 
return	vl_convertido_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
