CREATE OR REPLACE FUNCTION pls_convert_masc_cart_usuario ( cd_usuario_plano_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ds_mascara_w		varchar(255);
nr_cont_cart_w		bigint := 1;
i			bigint := 1;
				

BEGIN 
ds_mascara_w := pls_obter_mascara_cartao_benef(null,'O',cd_estabelecimento_p);
 
if (ds_mascara_w IS NOT NULL AND ds_mascara_w::text <> '') then 
	while(nr_cont_cart_w <> length(cd_usuario_plano_p) + 1) loop 
		begin	 
		 
		if (substr(ds_mascara_w, i, 1) in ('/','-','.',' ','_')) then 
			ds_retorno_w := ds_retorno_w || substr(ds_mascara_w, i, 1);
		else 
			ds_retorno_w := ds_retorno_w || substr(cd_usuario_plano_p, nr_cont_cart_w,1);
			nr_cont_cart_w := nr_cont_cart_w + 1;
		end if;	
		 
		i := i + 1;
		 
		end;
	end loop;
else 
	ds_retorno_w	:= cd_usuario_plano_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
