SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_coparticapaco_rdc_unimed ( nr_seq_plano_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p

T - Taxa percentual da coparticipacao ambulatorial
V - Valor diária da coparticipação do tipo internação

*/
vl_retorno_w			integer;
tx_co_participacao_amb_w	smallint;
vl_co_participacao_int_w	integer;



BEGIN

select	max(substr(CASE WHEN ie_tipo_atendimento='E' THEN coalesce(tx_coparticipacao,0)  ELSE 0 END ,1,2)),
	max(substr(CASE WHEN ie_tipo_atendimento='I' THEN coalesce(vl_coparticipacao,0)  ELSE 0 END ,1,5))
into STRICT	tx_co_participacao_amb_w,
	vl_co_participacao_int_w
from	pls_regra_coparticipacao
where	nr_seq_plano	= nr_seq_plano_p;

if (ie_opcao_p = 'T') then
	vl_retorno_w := tx_co_participacao_amb_w;
elsif (ie_opcao_p = 'V') then
	vl_retorno_w := vl_co_participacao_int_w;
end if;

if (ie_opcao_p = 'T') then
	return	tx_co_participacao_amb_w;
elsif (ie_opcao_p = 'V') then
	return	vl_retorno_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
