CREATE OR REPLACE FUNCTION pls_cta_nivel_grupo_analise ( nr_seq_grupo_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_nivel_liberacao_w		bigint;


BEGIN

begin
select	coalesce(a.nr_nivel_liberacao, b.nr_nivel_liberacao)
into STRICT	nr_nivel_liberacao_w
from	pls_grupo_auditor	b,
	pls_membro_grupo_aud	a
where	a.nr_seq_grupo		= b.nr_sequencia;
exception
when others then
	nr_nivel_liberacao_w	:= 10;
end;

if (coalesce(nr_nivel_liberacao_w,0) = 0) then
	nr_nivel_liberacao_w	:= 10;
end if;

return	nr_nivel_liberacao_w;

end;
