CREATE OR REPLACE FUNCTION pls_cta_obter_grupo_analise ( nr_seq_analise_p bigint, nm_usuario_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_retorno_p
	S - SequÃªncia
	N - Nome do grupo
*/
ds_retorno_w			varchar(120);
nr_seq_grupo_w			bigint;


BEGIN

select	max(a.nr_seq_grupo)
into STRICT	nr_seq_grupo_w
from	pls_membro_grupo_aud	a
where	a.nm_usuario_exec	= nm_usuario_p
and	ie_situacao		= 'A'
and	a.nr_seq_grupo in (	SELECT	x.nr_seq_grupo
				from	pls_auditoria_conta_grupo x
				where	nr_seq_analise	= nr_seq_analise_p);

if (ie_retorno_p	= 'S') then
	ds_retorno_w	:= nr_seq_grupo_w;
elsif (ie_retorno_p	= 'N') then
	select	max(nm_grupo_auditor)
	into STRICT	ds_retorno_w
	from	pls_grupo_auditor
	where	nr_sequencia	= nr_seq_grupo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
