SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_dados_guia_medico_web ( nr_seq_prestador_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

					 
/* 
Informar o retorno da function - ie_tipo_retorno_p 
B - Bairro 
*/
 
 
ds_retorno_w			varchar(255);
ie_tipo_endereco_w		varchar(5);
ds_bairro_w			varchar(255)	:= '';
cd_pessoa_fisica_w		varchar(10);
nr_seq_compl_adic_w		bigint;
cd_cgc_w			varchar(14);
nr_telefone_w			varchar(30);


BEGIN 
 
if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then 
	select	a.ie_tipo_endereco, 
		a.cd_pessoa_fisica, 
		a.cd_cgc, 
		coalesce(coalesce(nr_seq_compl_pf_tel_adic,nr_seq_compl_pj),0) 
	into STRICT	ie_tipo_endereco_w, 
		cd_pessoa_fisica_w, 
		cd_cgc_w, 
		nr_seq_compl_adic_w 
	from	pls_prestador a 
	where	a.nr_sequencia	= nr_seq_prestador_p;
		 
	 
	if (ie_tipo_endereco_w in ('PJ')) then 
		select	a.ds_bairro, 
			a.nr_telefone 
		into STRICT	ds_bairro_w, 
			nr_telefone_w 
		from	pessoa_juridica a 
		where	a.cd_cgc	= cd_cgc_w;
	elsif (ie_tipo_endereco_w in ('PJA')) and (nr_seq_compl_adic_w > 0) then 
		select	a.ds_bairro, 
			a.nr_telefone 
		into STRICT	ds_bairro_w, 
			nr_telefone_w 
		from	pessoa_juridica_compl a 
		where	a.cd_cgc	= cd_cgc_w	 
		and	a.nr_sequencia	= nr_seq_compl_adic_w;
	elsif (ie_tipo_endereco_w in ('PJC')) then 
		begin 
		select	a.ds_bairro, 
			a.nr_telefone 
		into STRICT	ds_bairro_w, 
			nr_telefone_w 
		from	pessoa_juridica_compl a 
		where	a.cd_cgc	= cd_cgc_w 
		and	a.ie_tipo_complemento = 1;
		exception 
		when others then 
			ds_bairro_w	:= null;
		end;
	elsif (ie_tipo_endereco_w in ('PJF')) then 
		begin 
		select	a.ds_bairro, 
			a.nr_telefone 
		into STRICT	ds_bairro_w, 
			nr_telefone_w 
		from	pessoa_juridica_compl a 
		where	a.cd_cgc	= cd_cgc_w 
		and	a.ie_tipo_complemento = 2;
		exception 
		when others then 
			ds_bairro_w	:= null;
		end;
	elsif (ie_tipo_endereco_w = 'PFR') then 
		select	a.ds_bairro, 
			a.nr_telefone 
		into STRICT	ds_bairro_w, 
			nr_telefone_w 
		from	compl_pessoa_fisica a 
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w 
		and	a.ie_tipo_complemento = 1;
	elsif (ie_tipo_endereco_w = 'PFC') then 
		select	a.ds_bairro, 
			a.nr_telefone 
		into STRICT	ds_bairro_w, 
			nr_telefone_w 
		from	compl_pessoa_fisica a 
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w 
		and	a.ie_tipo_complemento = 2;
		 
		if (nr_seq_compl_adic_w > 0) then 
			select	a.ds_bairro, 
				a.nr_telefone 
			into STRICT	ds_bairro_w, 
				nr_telefone_w 
			from	compl_pf_tel_adic a 
			where	a.nr_sequencia	= nr_seq_compl_adic_w;	
		end if;	
	end if;
end if;
 
if (ie_tipo_retorno_p = 'B') then 
	ds_retorno_w := ds_bairro_w;
elsif (ie_tipo_retorno_p = 'T') then 
	ds_retorno_w := nr_telefone_w;
end if;
 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_dados_guia_medico_web ( nr_seq_prestador_p bigint, ie_tipo_retorno_p text) FROM PUBLIC;

