CREATE OR REPLACE FUNCTION pls_dados_prest_med_espec_adic ( nr_sequencia_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
	A 	- Área de atuação 
	CDE 	- Especialidade 
	RCE 	- RCE 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ds_espec_medic_w	varchar(255);


BEGIN 
if (ie_tipo_p = 'A') then 
	begin 
	select	substr(coalesce(b.ds_area_atuacao, ''),1,255) 
	into STRICT	ds_espec_medic_w 
	from	area_atuacao_medica 	b, 
		pls_prestador_med_espec a 
	where	a.nr_seq_area_atuacao	= b.nr_sequencia 
	and	a.nr_sequencia		= nr_sequencia_p 
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));
	end;
elsif (ie_tipo_p = 'CDE') then 
	begin 
	select	max(a.cd_especialidade) 
	into STRICT	ds_espec_medic_w 
	from	pls_prestador_med_espec	a 
	where	a.nr_sequencia 	= nr_sequencia_p 
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));
	end;
elsif (ie_tipo_p = 'RCE') then 
	begin 
	select	max(b.nr_rqe) 
	into STRICT	ds_espec_medic_w 
	from	medico_especialidade	b, 
		pls_prestador_med_espec a 
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	a.cd_especialidade	= b.cd_especialidade 
	and	a.nr_sequencia 		= nr_sequencia_p 
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));
	end;
end if;
 
return ds_espec_medic_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
