CREATE OR REPLACE FUNCTION pls_data_reaj_nao_liberada (nr_seq_lote bigint) RETURNS varchar AS $body$
DECLARE


qt_dt_nao_liberadas	bigint;
ds_retorno_w		varchar(2);


BEGIN

select	count(*)
into STRICT	qt_dt_nao_liberadas
from	pls_segurado_preco_origem
where	nr_seq_lote_reajuste	= nr_seq_lote
and	coalesce(dt_liberacao::text, '') = '';

if (qt_dt_nao_liberadas > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
