SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_desc_cabecalho_relat_241 ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);
ds_endereco_numero_w		varchar(255);
ds_endereco_2_w			varchar(255);
ds_cnpj_inscr_est_w		varchar(255);
ds_fone_fax_w			varchar(255);


BEGIN

select	substr(ds_endereco || ',' || nr_endereco || '-',1,255) ds_endereco_numero,
	substr(cd_cep || ' ' || ds_municipio || ' - ' || sg_estado,1,255) ds_endereco_2,
	ds_observacao_mensalidade || '       CNPJ: 82.624.776/0001-47' ds_observacao_mensalidade,
	ds_tributo
into STRICT	ds_endereco_numero_w,
	ds_endereco_2_w,
	ds_cnpj_inscr_est_w,
	ds_fone_fax_w
from	w_pls_relat_boleto
where	nm_usuario = nm_usuario_p
and	ie_banda = 'CO'  LIMIT 1;

ds_retorno_w	:= 	ds_cnpj_inscr_est_w || chr(10) ||
			ds_endereco_numero_w || chr(10) ||
			ds_endereco_2_w || chr(10) ||
			ds_fone_fax_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
