CREATE OR REPLACE FUNCTION pls_desc_contrato ( nr_seq_contrato_p contrato.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE


ds_contrato_w contrato.ds_objeto_contrato%type;


BEGIN
	if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
		select max(nr_sequencia||' - '||ds_objeto_contrato)
		into STRICT ds_contrato_w
		from contrato
		where nr_sequencia = nr_seq_contrato_p;
	end if;
	return ds_contrato_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
