CREATE OR REPLACE FUNCTION pls_desc_nm_estipulante ( nr_seq_contrato_p bigint, nr_contrato_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_estipulante_w		varchar(255);

BEGIN
if (coalesce(nr_seq_contrato_p,0) <> 0) then 
	select	substr(obter_nome_pf_pj(cd_pf_estipulante, cd_cgc_estipulante),1,200) 
	into STRICT	nm_estipulante_w 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_p;
elsif (coalesce(nr_contrato_p,0)	<> 0) then 
	select	substr(obter_nome_pf_pj(cd_pf_estipulante, cd_cgc_estipulante),1,200) 
	into STRICT	nm_estipulante_w 
	from	pls_contrato 
	where	nr_contrato	= nr_contrato_p;
end if;
 
return	nm_estipulante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
