CREATE OR REPLACE FUNCTION pls_dias_intern_faixa_etaria ( qt_idade_inicial_p bigint, qt_idade_final_p bigint, ie_tipo_data_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_conta_p text) RETURNS bigint AS $body$
DECLARE


qt_media_diarias_w		double precision;


BEGIN

qt_media_diarias_w	:= 0;

select	avg(CASE WHEN qt_diarias=0 THEN 1  ELSE qt_diarias END )
into STRICT	qt_media_diarias_w
from	(
	SELECT	sum(obter_dias_entre_datas(c.dt_entrada,c.dt_alta)) qt_diarias,
		c.cd_guia_referencia
	from	pls_conta		c,
		pls_segurado		b,
		pls_protocolo_conta	a
	where	b.nr_sequencia	= c.nr_seq_segurado
	and	a.nr_sequencia	= c.nr_seq_protocolo
	and	coalesce(c.ie_tipo_conta, 'O') = ie_tipo_conta_p
	and	c.ie_tipo_guia		= '5'
	and	ie_tipo_protocolo	= 'C'
	and	((ie_tipo_data_p = 'P' and a.dt_protocolo between dt_inicial_p and fim_dia(dt_final_p)) or (ie_tipo_data_p = 'M' and a.dt_mes_competencia between dt_inicial_p and fim_dia(dt_final_p)) or (ie_tipo_data_p = 'N'))
	and	obter_idade_pf(b.cd_pessoa_fisica,c.dt_atendimento_referencia,'A') >= qt_idade_inicial_p
	and	obter_idade_pf(b.cd_pessoa_fisica,c.dt_atendimento_referencia,'A') <= qt_idade_final_p
	and	pls_obter_se_internado(c.nr_sequencia, c.ie_tipo_conta) = 'S'
	group by c.cd_guia_referencia
) alias13;

return	qt_media_diarias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
