CREATE OR REPLACE FUNCTION pls_elimina_zeros_esquerda (ds_valor_p text) RETURNS varchar AS $body$
DECLARE



tamanho_w		integer;
ds_retorno_w		varchar(2000);
ds_aux_retorno_w	varchar(2000);

BEGIN

begin
tamanho_w	:= coalesce(Length(ds_valor_p), 0);
ds_retorno_w	:= ds_valor_p;
ds_aux_retorno_w := ds_valor_p;

FOR i IN 1..tamanho_w LOOP
	if (substr(ds_retorno_w,i,1)='0') then
		ds_aux_retorno_w := substr(ds_retorno_w,i+1,tamanho_w);
	else
		exit;
	end if;
END LOOP;
exception
when others then
	/* #@DS_ERRO#@
	ds_valor_p=#@DS_VALOR#@ */
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266943, 'DS_ERRO=' || sqlerrm || ';DS_VALOR=' || ds_valor_p);
end;

return ds_aux_retorno_w;

end;
$body$
