CREATE OR REPLACE FUNCTION pls_email_vendedor_analise ( nr_seq_analise_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w			varchar(255);


BEGIN 
 
if (coalesce(obter_valor_param_usuario(1236,22, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento),'P') = 'P') then 
	select	substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'M'),1,255) 
	into STRICT	ds_retorno_w 
	from	pls_analise_adesao a, 
		pls_proposta_adesao b, 
		pls_vendedor c 
	where	a.nr_seq_proposta	= b.nr_sequencia 
	and	b.nr_seq_vendedor_canal	= c.nr_sequencia 
	and	a.nr_sequencia		= nr_seq_analise_p;
else 
	select	max(d.ds_email) 
	into STRICT	ds_retorno_w 
	from	pls_analise_adesao	a, 
		pls_proposta_adesao	b, 
		pls_vendedor 		c, 
		usuario			d 
	where	a.nr_seq_proposta	= b.nr_sequencia 
	and	b.nr_seq_vendedor_canal	= c.nr_sequencia 
	and	d.cd_pessoa_fisica	= c.cd_pessoa_fisica 
	and	a.nr_sequencia		= nr_seq_analise_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
