SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_esocial_obter_valor_tot ( nr_seq_esocial_lote_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
R - Rendimento
BI - Base INSS
IN - INSS
BR - Base IR
IR - IR
*/
vl_rendimento_w		esocial_dados_pagamento.vl_rendimento_bruto%type;
vl_base_inss_w		esocial_dados_pagamento.vl_base_inss%type;
vl_inss_w		esocial_dados_pagamento.vl_trib_inss%type;
vl_base_ir_w		esocial_dados_pagamento.vl_base_irf%type;
vl_ir_w			esocial_dados_pagamento.vl_trib_ir%type;

vl_retorno_w		double precision;


BEGIN

begin
	select	sum(vl_rendimento_bruto),
		sum(vl_base_inss),
		sum(vl_trib_inss),
		sum(vl_base_irf),
		sum(vl_trib_ir)
	into STRICT	vl_rendimento_w,
		vl_base_inss_w,
		vl_inss_w,
		vl_base_ir_w,
		vl_ir_w
	from 	esocial_dados_pagamento
	where	nr_seq_esocial_lote = nr_seq_esocial_lote_p;

exception
when others then
	vl_rendimento_w	:= 0;
	vl_base_inss_w	:= 0;
	vl_inss_w	:= 0;
	vl_base_ir_w	:= 0;
	vl_ir_w		:= 0;
end;

vl_retorno_w:= 0;

case ie_opcao_p
		when 'R' then
			vl_retorno_w:= vl_rendimento_w;
		when 'BI' then
			vl_retorno_w:= vl_base_inss_w;
		when 'IN' then
			vl_retorno_w:= vl_inss_w;
		when 'BR' then
			vl_retorno_w:= vl_base_ir_w;
		when 'IR' then
			vl_retorno_w:= vl_ir_w;
end case;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
