CREATE OR REPLACE FUNCTION pls_existe_primeira_mens ( nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= 'N';
qt_registros_w		integer;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_mensalidade	a,
	pls_lote_mensalidade b
where	a.nr_seq_lote		= b.nr_sequencia
and	b.nr_seq_contrato	= nr_seq_contrato_p
and	b.ie_primeira_mensalidade = 'S'
and	coalesce(a.ie_cancelamento::text, '') = '';


if (qt_registros_w > 0) then
	ds_retorno_w	:= 'S';
else
	select	count(1)
	into STRICT	qt_registros_w
	
	where	exists (	SELECT	1
			from	pls_contrato_pagador	a,
				pls_mensalidade		b,
				pls_lote_mensalidade	c
			where	b.nr_seq_pagador	= a.nr_sequencia
			and	b.nr_seq_lote		= c.nr_sequencia
			and	a.nr_seq_contrato	= nr_seq_contrato_p
			and	coalesce(b.ie_cancelamento::text, '') = '');
	
	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_existe_primeira_mens ( nr_seq_contrato_p bigint) FROM PUBLIC;

