SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_extrair_dado_tag_xml ( ds_conteudo_p text, ds_tag_p text, ie_tipo_p text default 1) RETURNS varchar AS $body$
DECLARE


ds_tag_fechamento_w		varchar(255);
qt_inicial_w			integer;
qt_final_w			integer;
ds_retorno_w			varchar(4000);

ds_conteudo_w		varchar(4000);


BEGIN
ds_retorno_w	:= '';

if (ie_tipo_p = 1) then --Conteúdo da tag
	if (position(ds_tag_p in ds_conteudo_p) > 0) then
		ds_tag_fechamento_w	:= '</'||substr(ds_tag_p,2,length(ds_tag_p));
		qt_inicial_w		:= position(ds_tag_p in ds_conteudo_p)+length(ds_tag_p);
		qt_final_w		:= position(ds_tag_fechamento_w in ds_conteudo_p)-qt_inicial_w;
		ds_retorno_w	:= substr(ds_conteudo_p,qt_inicial_w,qt_final_w);
	else
		ds_retorno_w	:= '';
	end if;
elsif (ie_tipo_p = 2) then --Valores na declaração da tag
	ds_conteudo_w	:= ds_conteudo_p;

	select  position(ds_tag_p in ds_conteudo_w)
	into STRICT    qt_inicial_w
	;

	if (qt_inicial_w > 0) then
		ds_conteudo_w	:= substr(ds_conteudo_w,(qt_inicial_w+length(ds_tag_p)+2));
		ds_retorno_w	:= substr(ds_conteudo_w,1,position('"' in ds_conteudo_w)-1);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
