SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_formatar_contas_analise ( ds_conta_p text ) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(4000);
ds_conta_w			varchar(255);
ds_seq_conta_w			varchar(10);
cd_guia_w			varchar(20);
nm_prestador_exec_w		varchar(255);
				

BEGIN 
 
ds_conta_w	:= ds_conta_p || ',';
 
while(position(',' in ds_conta_w) <> 0) loop 
	begin			 
	 
	--obtem-se a primeira conta 
	ds_seq_conta_w	:= substr(ds_conta_w,1,position(',' in ds_conta_w)-1);
	--remove-se o procedimento do conjunto 
	ds_conta_w	:= substr(ds_conta_w,position(',' in ds_conta_w)+1,255);	
	 
	select	cd_guia, 
		substr(pls_obter_dados_prestador(nr_seq_prestador_exec,'N'),1,255) -- limite máximo 
	into STRICT	cd_guia_w, 
		nm_prestador_exec_w 
	from	pls_conta 
	where	nr_sequencia = (ds_seq_conta_w)::numeric;
	 
	ds_retorno_w := ds_retorno_w || 'Guia: ' || cd_guia_w || chr(9) ||'Conta: ' || ds_seq_conta_w || chr(9) ||'Prestador exec.: ' || nm_prestador_exec_w;
 
 	/*Verifica-se se vai existir mais uma linha. Se sim então é inserido uma linha nova*/
 
	if (position(',' in ds_conta_w) <> 0) then 
		ds_retorno_w := ds_retorno_w || chr(13);
	end if;
		 
	end;	
end loop;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_formatar_contas_analise ( ds_conta_p text ) FROM PUBLIC;

