CREATE OR REPLACE FUNCTION pls_formatar_minutos ( nr_minutos_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dias_w	bigint;
qt_horas_w	bigint;
qt_tempo_w	double precision;
qt_minutos_w	bigint;
qt_minutos_ww	double precision;
qt_segundo_w	double precision;
ds_horario_w	varchar(255);
qt_segundo_ww	varchar(2);


BEGIN
qt_horas_w	:= 0;
qt_dias_w	:= 0;

qt_tempo_w	:= nr_minutos_p;

qt_minutos_w	:= trunc(qt_tempo_w);

qt_minutos_ww	:= trunc(qt_tempo_w);

qt_segundo_w	:= elimina_caracteres_especiais(trunc(qt_tempo_w,2) - qt_minutos_ww);

qt_segundo_ww	:= lpad(elimina_caracteres_especiais(qt_segundo_w / 1000),2,'0');

while(qt_minutos_w >= 60) loop
	qt_horas_w   := qt_horas_w + 1;
	qt_minutos_w := qt_minutos_w - 60;
end loop;

while(qt_horas_w >= 24) loop
	qt_dias_w  := qt_dias_w + 1;
	qt_horas_w := qt_horas_w - 24;
end loop;

if (qt_dias_w > 0) then
	ds_horario_w := ds_horario_w || qt_dias_w || ' d ';
end if;

if (qt_horas_w > 0) then
	ds_horario_w := ds_horario_w || lpad(qt_horas_w, 2, '0');

	if (qt_minutos_w > 0) then
		ds_horario_w := ds_horario_w || ':' || lpad(qt_minutos_w, 2, '0') || ':' || lpad(qt_segundo_ww,2,'0');
	else
		ds_horario_w := ds_horario_w || ':00:00';
	end if;
else
	if (qt_minutos_w > 0) then
		ds_horario_w := ds_horario_w || '00:' || lpad(elimina_caracteres_especiais(qt_minutos_w), 2, '0') || ':' || lpad(qt_segundo_ww,2,'0');
	else
		ds_horario_w := ds_horario_w || '00:' || '00' || ':' || lpad(qt_segundo_ww,2,'0');
	end if;
end if;

return	ds_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
