CREATE OR REPLACE FUNCTION pls_format_dt_fim_relat_02425 (tipo_data_p bigint, data_p timestamp) RETURNS timestamp AS $body$
DECLARE


retorno_w	timestamp;

/*
tipo_data_p:
1 e 4  -  retorna último dia do mês    recebe XX/XX/XXXX
2 e 5  -  retorna último dia do ano    recebe 01/01/XXXX
*/
BEGIN

if (tipo_data_p in (1,4)) then --Mês/Período Mês
	retorno_w := last_day(data_p);
elsif (tipo_data_p in (2,5)) then --Anual/ Período Anual
	retorno_w := (data_p + 364);
end if;

return	retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
