CREATE OR REPLACE FUNCTION pls_forma_cobranca_afpergs ( nr_seq_proposta_pagador_p bigint, nr_seq_forma_cobranca_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);
cd_banco_w			integer;
cd_agencia_bancaria_w		varchar(8);
cd_conta_w			varchar(20);
nr_seq_empresa_w		bigint;
nr_seq_vinculo_empresa_w	bigint;
cd_matricula_w			varchar(20);


BEGIN

select	cd_banco,
	cd_agencia_bancaria,
	cd_conta,
	nr_seq_empresa,
	nr_seq_vinculo_empresa,
	cd_matricula
into STRICT	cd_banco_w,
	cd_agencia_bancaria_w,
	cd_conta_w,
	nr_seq_empresa_w,
	nr_seq_vinculo_empresa_w,
	cd_matricula_w
from	pls_proposta_pagador
where	nr_sequencia	= nr_seq_proposta_pagador_p;

if (nr_seq_forma_cobranca_p = 2) then
	ds_retorno_w	:= ' /  Banco : '|| substr(Obter_Nome_Banco(cd_banco_w),1,255) || ' /  AgÃªncia: ' || cd_agencia_bancaria_w || ' /  Conta Corrente: ' || cd_conta_w;
elsif (nr_seq_forma_cobranca_p = 4) then
	ds_retorno_w	:= ' / Empresa: '|| substr(pls_obter_dados_empresa(nr_seq_empresa_w,'D'),1,250) || ' / Vinculo empresa: ' || ' ' || ' / Matricula : '|| cd_matricula_w;
end if;

return	ds_retorno_w;

end;
