CREATE OR REPLACE FUNCTION pls_gerar_mensagem_boleto ( nr_seq_mensalidade_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(4000);			
dt_referencia_w		timestamp;
qt_titulos_aberto_w		bigint;
nr_seq_pagador_w		bigint;

			 

BEGIN 
 
select	nr_seq_pagador, 
	dt_referencia 
into STRICT	nr_seq_pagador_w, 
	dt_referencia_w 
from	pls_mensalidade 
where	nr_sequencia	= nr_seq_mensalidade_p;
 
select	count(*) 
into STRICT	qt_titulos_aberto_w 
from	pls_mensalidade a, 
	titulo_receber b 
where	a.nr_sequencia	= b.nr_seq_mensalidade 
and	a.dt_referencia	< dt_referencia_w 
and	b.ie_situacao	= '1' 
and	a.nr_seq_pagador	= nr_seq_pagador_w 
and	coalesce(a.ie_cancelamento::text, '') = '';
 
if (coalesce(qt_titulos_aberto_w,0) > 0) then 
	ds_retorno_w := 'Existem t√≠tulos abertos para o pagador ' ||substr(pls_obter_dados_pagador(nr_seq_pagador_w,'N'),1,255);
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
