CREATE OR REPLACE FUNCTION pls_gerar_mensagem_email_atx ( ie_tipo_email_p pls_mensagem_email.ie_tipo_email%type, ie_opcao_p text, nm_usuario_liberacao_p pls_mensagem_email.nm_usuario_liberacao%type, cd_perfil_p pls_mensagem_email.cd_perfil%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  retornar mensagem cadastrada em OPS - Cadastro de regras / mensalidade /
mensagem / email.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ x ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_mensagem_w		varchar(4000);
nr_sequencia_w		pls_mensagem_email.nr_sequencia%type;
ds_assunto_w		pls_mensagem_email.ds_assunto%type;
nr_seq_rtf_srtring_w	bigint;
ds_mensagem_ww		pls_mensagem_email.ds_mensagem%type;C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_assunto,
		ds_mensagem
	from	pls_mensagem_email
	where	ie_tipo_email = ie_tipo_email_p
	and	((cd_perfil = cd_perfil_p) or (coalesce(cd_perfil::text, '') = ''))
	and	((nm_usuario_liberacao = nm_usuario_liberacao_p) or (coalesce(nm_usuario_liberacao::text, '') = ''))
	order by coalesce(nm_usuario_liberacao, '') desc,
		coalesce(cd_perfil, '') desc;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ds_assunto_w,
	ds_mensagem_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	if (ie_opcao_p = 'A') then
		ds_mensagem_w	:= ds_assunto_w;
	elsif (ie_opcao_p = 'M') then
		nr_seq_rtf_srtring_w := converte_rtf_string('	select	ds_mensagem
					from	pls_mensagem_email
					where 	nr_sequencia = :nr_sequencia_w', nr_sequencia_w, nm_usuario_liberacao_p, nr_seq_rtf_srtring_w);
		select	ds_texto
		into STRICT	ds_mensagem_w
		from	tasy_conversao_rtf
		where	nr_sequencia = nr_seq_rtf_srtring_w;
	elsif (ie_opcao_p = 'ME') then
		ds_mensagem_w 	:= ds_mensagem_ww;
	end if;
end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
