SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_gerar_msg_titulo_aberto ( nr_seq_mensalidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
dt_referencia_w		timestamp;
qt_titulos_aberto_w		bigint;
nr_seq_pagador_w		bigint;
nr_titulo_w			bigint;
ie_situacao_w			varchar(2);
dt_referencia_mens_w		varchar(10);



BEGIN

select	nr_seq_pagador,
	dt_referencia
into STRICT	nr_seq_pagador_w,
	dt_referencia_w
from	pls_mensalidade
where	nr_sequencia	= nr_seq_mensalidade_p;

select	max(nr_titulo)
into STRICT	nr_titulo_w
from	pls_mensalidade a,
	titulo_receber b
where	a.nr_sequencia	= b.nr_seq_mensalidade
and	a.dt_referencia	< dt_referencia_w
and	a.nr_seq_pagador	= nr_seq_pagador_w
and	coalesce(a.ie_cancelamento::text, '') = '';

select	ie_situacao
into STRICT	ie_situacao_w
from	titulo_receber
where	nr_titulo	= nr_titulo_w;


select	to_char(clock_timestamp() - interval '30 days','MM/yyyy')
into STRICT	dt_referencia_mens_w
;


if (ie_situacao_w <> '1') then
	ds_retorno_w := '(OK) '||dt_referencia_mens_w;
elsif (ie_situacao_w = '1') then
	ds_retorno_w := '(Ultima mensalidade em aberto) '||dt_referencia_mens_w;
end if;




return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_gerar_msg_titulo_aberto ( nr_seq_mensalidade_p bigint) FROM PUBLIC;

