CREATE OR REPLACE FUNCTION pls_gerar_nome_abreviado ( nm_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= '';
i				bigint	:= 0;
pos_nome_w			bigint;
nm_pessoa_w			varchar(255);
ds_lista_nome_w			varchar(255);
qt_mais_nome_w			bigint;
ie_nome_ok_w			varchar(1)	:= 'N';
ds_ultima_letra_w		varchar(1);


BEGIN

ds_lista_nome_w	:= rtrim(ltrim(nm_pessoa_fisica_p));

/* Rotina para retirar os espaços em branco após o nome */

while(ie_nome_ok_w	= 'N') loop
	begin
	select	substr(ds_lista_nome_w,length(ds_lista_nome_w),length(ds_lista_nome_w))
	into STRICT	ds_ultima_letra_w
	;

	if (ds_ultima_letra_w = ' ') then
		ds_lista_nome_w	:= substr(ds_lista_nome_w,1,length(ds_lista_nome_w)-1);
	else
		ie_nome_ok_w	:= 'S';
	end if;

	end;
end loop;

/* Inserir um espaço em branco para delimitar o fim do nome */

ds_lista_nome_w	:= ds_lista_nome_w || ' ';

while(ds_lista_nome_w IS NOT NULL AND ds_lista_nome_w::text <> '') loop
	begin
	i	:= i+1; /* Variável "i" criada para não abreviar o primeiro nome */
	select	position(' ' in ds_lista_nome_w)
	into STRICT	pos_nome_w
	;

	if (pos_nome_w > 0) then
		nm_pessoa_w	:= substr(ds_lista_nome_w,1,pos_nome_w - 1);
		nm_pessoa_w	:= upper(nm_pessoa_w);

		ds_lista_nome_w	:= substr(ds_lista_nome_w,pos_nome_w + 1,length(ds_lista_nome_w));
		if (i > 1) then
			if (nm_pessoa_w = 'DA') or (nm_pessoa_w = 'DE') or (nm_pessoa_w = 'DI') or (nm_pessoa_w = 'DO') or (nm_pessoa_w = 'DU') or (nm_pessoa_w = 'DAS') or (nm_pessoa_w = 'DES') or (nm_pessoa_w = 'DIS') or (nm_pessoa_w = 'DOS') or (nm_pessoa_w = 'DUS') then --Eliminar as preposições do nome
				nm_pessoa_w	:= '';
			else
				--Verifica se é o último nome - O último nome não pode ser abreviado
				select	position(' ' in ds_lista_nome_w)
				into STRICT	qt_mais_nome_w
				;

				if (qt_mais_nome_w > 0) then
					nm_pessoa_w	:= upper(substr(nm_pessoa_w,1,1));
				end if;
			end if;
		end if;

		if (length(nm_pessoa_w) > 0) then
			ds_retorno_w	:= ds_retorno_w || nm_pessoa_w || ' ';
		end if;

	end if;
	end;

end loop;

ds_retorno_w	:= elimina_caractere_especial(ds_retorno_w);

return	substr(ds_retorno_w,1,length(ds_retorno_w)-1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
