CREATE OR REPLACE FUNCTION pls_grupo_contratual_equipe ( nr_seq_grupo_contr_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(2) := 'S';
qt_vidas_w		bigint;
nr_seq_equipe_w		bigint;
qt_vidas_incial_w	bigint;
qt_vidas_final_w	bigint;


BEGIN 
 
select	max(nr_seq_equipe) 
into STRICT	nr_seq_equipe_w 
from	pls_grupo_contrato_equipe 
where	clock_timestamp() between trunc(coalesce(dt_inicio_vigencia,clock_timestamp()),'dd') and coalesce(dt_fim_vigencia,clock_timestamp()) 
and	nr_seq_grupo_contrato	= nr_seq_grupo_contr_p;
 
select	coalesce(substr(pls_obter_dados_grupo_contrato(nr_seq_grupo_contr_p,'QV'),1,255),1000) 
into STRICT	qt_vidas_w
;
 
select	qt_vidas_inicial, 
	qt_vidas_final 
into STRICT	qt_vidas_incial_w, 
	qt_vidas_final_w 
from	pls_equipe_grupo_contrato 
where	nr_sequencia	= nr_seq_equipe_w 
and	ie_situacao	= 'A';
 
if ((qt_vidas_incial_w IS NOT NULL AND qt_vidas_incial_w::text <> '') or (qt_vidas_final_w IS NOT NULL AND qt_vidas_final_w::text <> '')) and (qt_vidas_w between qt_vidas_incial_w and qt_vidas_final_w) then 
	ie_retorno_w	:= 'S';
else 
	ie_retorno_w	:= 'N';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
