SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_identificar_reg_sib ( nr_seq_lote_sib_p bigint, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);
nr_seq_interno_w	bigint;
qt_cco_w		bigint;
qt_erro_w		bigint;
qt_atualizado_w		bigint;
qt_aguardando_erro_w	bigint;
qt_aguardando_cco_w	bigint;
qt_tot_aguradao_w	bigint := 0;



BEGIN

select	count(*)
into STRICT	qt_cco_w
from	sib_devolucao_cco
where	nr_seq_lote	= nr_seq_lote_sib_p
and	nr_seq_interno	= nr_seq_interno_p;

select	count(*)
into STRICT	qt_erro_w
from	sib_devolucao_erro
where	nr_seq_lote		= nr_seq_lote_sib_p
and	nr_seq_interno		= nr_seq_interno_p;

select	count(*)
into STRICT	qt_atualizado_w
from	pls_segurado a,
	pls_interf_sib b
where	b.nr_seq_lote_sib	= nr_seq_lote_sib_p
and	b.nr_sequencia		= nr_seq_interno_p
and	b.nr_seq_segurado 	= a.nr_sequencia
and	(a.nr_cco IS NOT NULL AND a.nr_cco::text <> '');

select	count(*)
into STRICT	qt_aguardando_cco_w
from	sib_devolucao_cco
where	nr_seq_lote	= nr_seq_lote_sib_p
and	nr_seq_interno	= nr_seq_interno_p;

select	count(*)
into STRICT	qt_aguardando_erro_w
from	sib_devolucao_erro
where	nr_seq_lote		= nr_seq_lote_sib_p
and	nr_seq_interno		= nr_seq_interno_p;

qt_tot_aguradao_w := qt_tot_aguradao_w + qt_aguardando_erro_w + qt_aguardando_cco_w;

if (qt_tot_aguradao_w = 0) then
	ds_retorno_w := 'R';
elsif (qt_atualizado_w > 0) then
	ds_retorno_w := 'A';
elsif (qt_cco_w > 0) then
	ds_retorno_w := 'C';
elsif (qt_erro_w > 0) then
	ds_retorno_w := 'E';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
