CREATE OR REPLACE FUNCTION pls_liberar_sub_lote_sib ( nr_seq_lote_sib_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_lotes_liberados_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_lotes_liberados_w
from	pls_lote_sib
where	nr_sequencia	= nr_seq_lote_sib_p
and	ie_status	= 'E'
and	(dt_envio IS NOT NULL AND dt_envio::text <> '');

if (qt_lotes_liberados_w = 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
