CREATE OR REPLACE FUNCTION pls_libera_relat_lote (nr_seq_lote_p bigint, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w        varchar(2);
nr_seq_prestador_w    bigint;


BEGIN


select    coalesce(max(nr_sequencia),0)
into STRICT    nr_seq_prestador_w
from    pls_pag_prest_vencimento
where    nr_seq_pag_prestador = (SELECT    max(nr_sequencia)
                from    pls_pagamento_prestador
                where     nr_seq_lote        = nr_seq_lote_p
                and    nr_seq_prestador     = nr_seq_prestador_p);

if (nr_seq_prestador_w = 0) then
    ds_retorno_w := 'N';
else
    ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
