SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_manutencao_origem_carencia ( nr_seq_w_carencia_p bigint) RETURNS varchar AS $body$
DECLARE


/*
 aasschlote - 31/05/2010 - Function criada para obter a descrição a origem da carência
*/
ds_retorno_w		varchar(50);
ie_origem_carencia_w	varchar(10);


BEGIN

select	max(ie_origem_carencia)
into STRICT	ie_origem_carencia_w
from	w_pls_carencia
where	nr_sequencia	= nr_seq_w_carencia_p;

if (ie_origem_carencia_w = 'P') then
	ds_retorno_w := 'Produto';
elsif (ie_origem_carencia_w = 'C') then
	ds_retorno_w := 'Contrato';
elsif (ie_origem_carencia_w = 'BC') then
	ds_retorno_w := 'Beneficiário do contrato';
elsif (ie_origem_carencia_w = 'BP') then
	ds_retorno_w := 'Beneficiário da proposta';
elsif (ie_origem_carencia_w = 'M') then
	ds_retorno_w := 'Manual';
elsif (ie_origem_carencia_w = 'PA') then
	ds_retorno_w := 'Proposta de Adesão';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
