CREATE OR REPLACE FUNCTION pls_montar_estrutura_material (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ds_estrutura_w			varchar(100);
nr_seq_superior_w		bigint;
nr_seq_superior_ww		bigint;



BEGIN
select	nr_sequencia
into STRICT	nr_seq_superior_ww
from	pls_estrutura_material
where	nr_sequencia = nr_sequencia_p;

while(nr_seq_superior_ww IS NOT NULL AND nr_seq_superior_ww::text <> '') loop
	begin
	select	max(nr_seq_superior)
	into STRICT	nr_seq_superior_w
	from	pls_estrutura_material
	where	nr_sequencia = nr_seq_superior_ww;

	select	substr(ds_estrutura,1,40)
	into STRICT	ds_estrutura_w
	from	pls_estrutura_material
	where	nr_sequencia = nr_seq_superior_ww;

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_estrutura_w;
	else
		ds_retorno_w := ds_estrutura_w || ' / ' ||ds_retorno_w;
	end if;

	if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
		nr_seq_superior_ww := nr_seq_superior_w;
	else
		nr_seq_superior_ww := null;
	end if;
	end;
end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
