CREATE OR REPLACE FUNCTION pls_monta_campo_ref_aval ( nr_seq_item_p bigint, nr_seq_declaracao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_item_w		varchar(100);
nr_seq_item_w		bigint;
ds_resposta_w		varchar(150);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	med_item_avaliar
	where	nr_seq_item_ref	= nr_seq_item_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	a.ds_item
	into STRICT	ds_item_w
	from	med_item_avaliar_v a
	where	a.nr_sequencia	= nr_seq_item_w;

	select	max(CASE WHEN b.qt_resultado=0 THEN b.ds_resultado  ELSE '' END )
	into STRICT	ds_resposta_w
	FROM med_item_avaliar_v a
LEFT OUTER JOIN pls_declaracao_result b ON (a.nr_sequencia = b.nr_seq_item)
WHERE a.nr_sequencia	= nr_seq_item_w and b.nr_seq_avaliacao	= nr_seq_declaracao_p;

	ds_retorno_w := ds_retorno_w || ds_item_w || ' ' || ds_resposta_w || '     ';

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
