CREATE OR REPLACE FUNCTION pls_obs_glosa_an (nr_seq_analise_p pls_analise_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(4000);

C01 CURSOR FOR
	SELECT 	ds_glosa_ocorrencia,
			ds_observacao
	from 	w_pls_analise_glosa_ocor
	where 	nr_seq_analise = nr_seq_analise_p;

BEGIN


	for r_c01_w in C01 loop

		ds_retorno_w := ds_retorno_w ||	r_c01_w.ds_glosa_ocorrencia ||' - '||coalesce(r_c01_w.ds_observacao,'Sem observação') ||chr(13)||'.';
	end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
