CREATE OR REPLACE FUNCTION pls_obter_abran_int ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_cooperativa_p pls_congenere.nr_sequencia%type, nr_seq_outorgante_p pls_outorgante.nr_sequencia%type, nr_seq_plano_p pls_plano.nr_sequencia%type, ie_abrangencia_p pls_plano.ie_abrangencia%type) RETURNS varchar AS $body$
DECLARE


/*Está rotina irá consistir a abrangência para beneficiários de itercâmbio A500

Após retornar os dados, compara a abrangência da operadora congênere com a abrangência do produto do beneficiário. Se for informado a área de atuação utilizará ela, caso contrário seguirá o processo descrito abaixo.
Para a abrangência municipal do produto, o município da operadora congênere deve ser o mesmo da  cooperativa/operadora do beneficiário.
Já para uma abrangência estadual, o estado da operadora congênere deve ser o mesmo da  cooperativa/operadora do beneficiário.
Para a nacional, ambas devem estar dentro do mesmo país.
Para este tipo de intercâmbio poderão ser utilizadas todas as abrangências descritas no PTU. Se for utilizada alguma referente a grupos, obrigatoriamente deve ser informado a área de atuação.
*/
nr_seq_pais_cooperativa_w	pais.nr_sequencia%type;
nr_seq_pais_outorgante_w	pais.nr_sequencia%type;
cd_cgc_coop_w			pls_congenere.cd_cgc%type;
cd_cgc_outorgante_w		pls_outorgante.cd_cgc_outorgante%type;
qt_area_atuacao_w		integer;
ie_area_coberta_w		varchar(1);

/* Áreas de atuação do plano */

c01 CURSOR(nr_seq_plano_p	pls_plano.nr_sequencia%type) FOR
	SELECT	a.cd_municipio_ibge,
		a.sg_estado,
		a.nr_seq_regiao
	from	pls_plano_area a
	where	a.nr_seq_plano	= nr_seq_plano_p;

/* Áreas de atuação da operadora que enviou o arquivo*/

C02 CURSOR(nr_seq_cooperativa_p	pls_congenere.nr_sequencia%type) FOR
	SELECT	a.cd_municipio_ibge,
		a.sg_estado,
		a.nr_seq_regiao
	from	pls_cooperativa_area	a
	where	a.nr_seq_cooperativa	= nr_seq_cooperativa_p;

/*Área de atuação da cooperativa que recebeu o arquivo*/

c03 CURSOR(nr_seq_outorgante_p	pls_outorgante.nr_sequencia%type) FOR
	SELECT	a.cd_municipio_ibge,
		a.sg_uf_municipio,
		a.nr_seq_regiao
	from	pls_regiao_atuacao a
	where	a.nr_seq_operadora	= nr_seq_outorgante_p;
BEGIN
/*Se não consistir nada abaixo, irá retornar que não tem abrangência por padrão. Princípio da pior situação*/

ie_area_coberta_w		:= 'N';

/*Verificação de segurança das informações */

if ((ie_abrangencia_p IS NOT NULL AND ie_abrangencia_p::text <> '') and (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') and (nr_seq_cooperativa_p IS NOT NULL AND nr_seq_cooperativa_p::text <> ''))	then

	/*Verificar se no sistema está informado a area de atuação do produto*/

	select	count(1)
	into STRICT	qt_area_atuacao_w
	from	pls_plano_area	a
	where	nr_seq_plano = nr_seq_plano_p;

	if (ie_abrangencia_p <> 'N')	then
		/*Se houver área de atuação informada para o plano*/

		if (qt_area_atuacao_w > 0)	then
			for r_c01 in C01(nr_seq_plano_p) loop/*informações do plano r_C01*/
				for r_C02 in C02(nr_seq_cooperativa_p) loop/*informações da operadora do atendimento r_c02*/
					/*Compara o municipio da operadora com o municipio da cooperativa*/

					if (ie_abrangencia_p = 'M')	then
						if (r_c01.cd_municipio_ibge = r_c02.cd_municipio_ibge)	then
							ie_area_coberta_w  := 'S';
							return ie_area_coberta_w;
						end if;
					/*Compara a abrangência estadual, estado do plano com o estado da cooperativa do beneficiário */

					elsif (ie_abrangencia_p = 'E')	then
						if (r_c01.sg_estado = r_c02.sg_estado)	then
							ie_area_coberta_w := 'S';
							return ie_area_coberta_w;
						end if;
					elsif (ie_abrangencia_p = 'GM')	then/*Grupo de múnicipios*/
						if (r_c01.nr_seq_regiao = r_c02.nr_seq_regiao) then
							ie_area_coberta_w	:= 'S';
							return ie_area_coberta_w;
						/*Se o município da operadora executora está na região do plano*/

						elsif (pls_obter_se_mun_uf_regiao(r_c02.cd_municipio_ibge, null, r_c01.nr_seq_regiao) = 'S') then
							ie_area_coberta_w	:= 'S';
							return ie_area_coberta_w;

						/*Se o município  do plano está na região da operadora executora*/

						elsif (pls_obter_se_mun_uf_regiao(r_c01.cd_municipio_ibge, null, r_c02.nr_seq_regiao) = 'S') then
							ie_area_coberta_w	:= 'S';
							return ie_area_coberta_w;
						end if;

					elsif (ie_abrangencia_p = 'GE')	then

						if (r_c01.nr_seq_regiao = r_c02.nr_seq_regiao) then
							ie_area_coberta_w	:= 'S';
							return ie_area_coberta_w;
						/*Se o  estado da operadora executora está na região do plano*/

						elsif (pls_obter_se_mun_uf_regiao(null, r_c02.sg_estado ,r_c01.nr_seq_regiao) = 'S') then
							ie_area_coberta_w	:= 'S';
							return ie_area_coberta_w;

						/*Se  o estado do plano está na região da operadora executora*/

						elsif (pls_obter_se_mun_uf_regiao(null,r_c01.sg_estado ,r_c02.nr_seq_regiao) = 'S') then
							ie_area_coberta_w	:= 'S';
							return ie_area_coberta_w;
						end if;
					end if;
				end loop;
			end loop;

		elsif (nr_seq_outorgante_p IS NOT NULL AND nr_seq_outorgante_p::text <> '')	then
			for r_c03 in C03(nr_seq_outorgante_p) loop/*informações da operadora que recebeu o arquivo r_c03*/
				for r_C02 in C02(nr_seq_cooperativa_p) loop/*informações da operadora do atendimento r_c02*/
					ie_area_coberta_w	:= 'S';

					/*Compara a abrangência municipal, compara a cidade da operadora benef com a cidade da cooperativa do beneficiário */

					if (ie_abrangencia_p = 'M')	then
						if (r_c03.cd_municipio_ibge = r_c02.cd_municipio_ibge)	then
							ie_area_coberta_w  := 'S';
							return ie_area_coberta_w;
						end if;
					/*Compara a abrangência estadual, compara estado da operadora benef com o estado da cooperativa do beneficiário */

					elsif (ie_abrangencia_p = 'E')	then
						if (r_c03.sg_uf_municipio = r_c02.sg_estado)	then
							ie_area_coberta_w := 'S';
							return ie_area_coberta_w;
						end if;
					end if;

				end loop;
			end loop;
		end if;
	/*Consistência da abrangência nacional*/

	elsif (ie_abrangencia_p = 'N')	then

		/*Buscar o código da outorgante*/

		select 	max(x.cd_cgc_outorgante)
		into STRICT	cd_cgc_outorgante_w
		from	pls_outorgante	x
		where	x.nr_sequencia = nr_seq_outorgante_p;

		/*Buscar o código da operadora que envio o arquivo*/

		select	max(x.cd_cgc)
		into STRICT	cd_cgc_coop_w
		from	pls_congenere	x
		where	x.nr_sequencia = nr_seq_cooperativa_p;

		/*Buscar os países*/

		nr_seq_pais_cooperativa_w	:= pls_obter_pais_abrang(cd_cgc_coop_w,'PJ');
		nr_seq_pais_outorgante_w	:= pls_obter_pais_abrang(cd_cgc_outorgante_w,'PJ');

		/*Compara se o país da cooperativa é igual ao país do outorgante*/

                if (nr_seq_pais_cooperativa_w IS NOT NULL AND nr_seq_pais_cooperativa_w::text <> '')      then
                        if (nr_seq_pais_cooperativa_w = nr_seq_pais_outorgante_w)	then
                                ie_area_coberta_w := 'S';
                                return ie_area_coberta_w;
                        end if;
                else
                        ie_area_coberta_w := 'S';
                        return ie_area_coberta_w;
                end if;
	end if;
end if;

return ie_area_coberta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_abran_int ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_cooperativa_p pls_congenere.nr_sequencia%type, nr_seq_outorgante_p pls_outorgante.nr_sequencia%type, nr_seq_plano_p pls_plano.nr_sequencia%type, ie_abrangencia_p pls_plano.ie_abrangencia%type) FROM PUBLIC;

