CREATE OR REPLACE FUNCTION pls_obter_acao_auditor_oco ( nm_usuario_p text, cd_ocorrencia_p text, nr_seq_grupo_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_ocorrencia_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_ocorrencia_w
from	pls_ocorrencia
where	cd_ocorrencia = cd_ocorrencia_p;

select	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pls_membro_grupo_aud a,
	pls_ocorrencia_grupo b
where	a.nr_seq_grupo			= b.nr_seq_grupo
and	coalesce(a.nm_usuario_exec,'X') 	= nm_usuario_p
and	a.nr_seq_grupo		 	= nr_seq_grupo_p
and 	b.nr_seq_ocorrencia 		= nr_seq_ocorrencia_w
and	a.ie_situacao = 'A'
and	b.ie_situacao = 'A';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
