SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_analise_grupo_atual ( nr_seq_analise_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

 
nr_fatura_ptu_w			ptu_fatura.nr_fatura%type;
ie_pre_analise_w		varchar(1);
nr_seq_grupo_atual_w		bigint;
nr_seq_conta_w			bigint;

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 

BEGIN 
select	pls_obter_conta_principal(a.cd_guia, a.nr_sequencia, a.nr_seq_segurado, a.nr_seq_prestador), 
	coalesce(ie_pre_analise,'N') 
into STRICT	nr_seq_conta_w, 
	ie_pre_analise_w 
from	pls_analise_conta	a 
where	a.nr_sequencia	= nr_seq_analise_p;
 
select	substr(pls_obter_dados_ptu_fatura(a.nr_seq_fatura,'F'),1,255) 
into STRICT	nr_fatura_ptu_w 
from	pls_conta	a 
where	a.nr_sequencia	= nr_seq_conta_w;
 
if (ie_pre_analise_w = 'S') and (nr_fatura_ptu_w IS NOT NULL AND nr_fatura_ptu_w::text <> '') then 
	select	coalesce(max(a.nr_seq_grupo),0) 
	into STRICT	nr_seq_grupo_atual_w 
	from	pls_auditoria_conta_grupo a 
	where	a.nr_seq_analise	= nr_seq_analise_p 
	and	(( 
		--pls_obter_se_auditor_grupo(a.nr_seq_grupo, nm_usuario_p) ='S' 
		(exists (SELECT	1 
				from	pls_membro_grupo_aud 
				where	nr_seq_grupo	= a.nr_seq_grupo 
				and	nm_usuario_exec	= nm_usuario_p 
				and	ie_situacao	= 'A')) or (a.nm_auditor_atual = nm_usuario_p)) and 
		coalesce(a.ie_pre_analise, 'N') = 'S') and 
		coalesce(a.dt_liberacao::text, '') = '' and 
		a.nr_seq_ordem	=	(select	min(x.nr_seq_ordem) 
					from	pls_auditoria_conta_grupo x 
					where	x.nr_seq_analise     = nr_seq_analise_p 
					and	coalesce(x.ie_pre_analise, 'N') = 'S' 
					and	coalesce(x.dt_liberacao::text, '') = '');
else 
	select	coalesce(max(a.nr_seq_grupo),0) 
	into STRICT	nr_seq_grupo_atual_w 
	from	pls_auditoria_conta_grupo a 
	where	a.nr_seq_analise	= nr_seq_analise_p 
	--and	((pls_obter_se_auditor_grupo(a.nr_seq_grupo, nm_usuario_p) = 'S') or 
	and	((exists (SELECT	1 
				from	pls_membro_grupo_aud 
				where	nr_seq_grupo	= a.nr_seq_grupo 
				and	nm_usuario_exec	= nm_usuario_p 
				and	ie_situacao	= 'A')) or (a.nm_auditor_atual = nm_usuario_p)) 
	and	coalesce(a.ie_pre_analise, 'N')	= 'N' 
	and	coalesce(a.dt_liberacao::text, '') = '' 
	and	((not exists (select	x.nr_sequencia 
				from	pls_auditoria_conta_grupo x 
				where	x.nr_seq_analise     = nr_seq_analise_p 
				and	coalesce(x.ie_pre_analise, 'N') = 'N' 
				and	(x.nm_auditor_atual IS NOT NULL AND x.nm_auditor_atual::text <> '') 
				and	coalesce(x.dt_liberacao::text, '') = '' 
				and	x.nr_seq_ordem	= a.nr_seq_ordem)) or (a.nm_auditor_atual	= nm_usuario_p)) 
	and	a.nr_seq_ordem	=	(select	min(x.nr_seq_ordem) 
					from	pls_auditoria_conta_grupo	x 
					where	x.nr_seq_analise     = nr_seq_analise_p 
					and	coalesce(x.ie_pre_analise, 'N') = 'N' 
					and	coalesce(x.dt_liberacao::text, '') = '');
end if;
 
return	nr_seq_grupo_atual_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_analise_grupo_atual ( nr_seq_analise_p bigint, nm_usuario_p text) FROM PUBLIC;

