SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_area_cobranca ( nr_seq_segurado_p bigint, cd_estabelecimento_p text) RETURNS varchar AS $body$
DECLARE


cd_cooperativa_w	varchar(10);
cd_cooperativa_ww	varchar(10);
ie_tipo_segurado_w	varchar(10);
ie_tipo_repasse_w	varchar(10);


BEGIN

select	ie_tipo_segurado
into STRICT	ie_tipo_segurado_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

select	max(cd_cooperativa)
into STRICT	cd_cooperativa_ww
from	pls_congenere	b,
	pls_outorgante	a
where	b.cd_cgc		= a.cd_cgc_outorgante
and	a.cd_estabelecimento	= cd_estabelecimento_p;

if (ie_tipo_segurado_w not in ('C','R','I','H')) then
	cd_cooperativa_w	:= cd_cooperativa_ww;
else
	select	max(ie_tipo_repasse)
	into STRICT	ie_tipo_repasse_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;

	if (ie_tipo_repasse_w	= 'C') then
		select	max(b.cd_cooperativa)
		into STRICT	cd_cooperativa_ww
		from	pls_congenere		b,
			pls_segurado		a
		where	a.nr_seq_congenere	= b.nr_sequencia
		and	a.nr_sequencia		= nr_seq_segurado_p;
	end if;

	cd_cooperativa_w	:= cd_cooperativa_ww;
end if;

return	cd_cooperativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
