CREATE OR REPLACE FUNCTION pls_obter_assinatura_email_web (ie_funcao_regra_p bigint, ie_quebrar_linha_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(4000);
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN
if ((cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and cd_estabelecimento_p > 0) then
	cd_estabelecimento_w	:= cd_estabelecimento_p;
end if;

select	substr(max(ds_assinatura),1,4000)
into STRICT	ds_retorno_w
from	pls_assinatura_email_web
where (ie_funcao_regra = ie_funcao_regra_p or ie_funcao_regra = 1)
and	ie_situacao = 'A'
and (coalesce(cd_estabelecimento_w::text, '') = ''
or	cd_estabelecimento	= cd_estabelecimento_w);

if ((ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and ie_quebrar_linha_p = 'S') then
	ds_retorno_w	:= replace(ds_retorno_w, '\n', '<br/>');
elsif ((ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and ie_quebrar_linha_p = 'N') then
	ds_retorno_w	:= replace(ds_retorno_w, '\n', '');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
