SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_auditor_atual_fluxo ( nr_seq_analise_p bigint, nr_Seq_grupo_atual_p bigint) RETURNS varchar AS $body$
DECLARE


nm_auditor_atual_w		varchar(255):= '';
nr_seq_ordem_auditor_w		bigint;


BEGIN

if ( coalesce(nr_seq_analise_p,0) > 0) and (coalesce(nr_Seq_grupo_atual_p,0)>0) then

	select	max(nm_auditor_atual)
	into STRICT	nm_auditor_atual_w
	from 	pls_auditoria_conta_grupo
	where	nr_seq_analise 	= nr_seq_analise_p
	and	nr_Seq_grupo 	= nr_Seq_grupo_atual_p
	and	nr_seq_ordem	= (	SELECT  min(nr_seq_ordem)
					from 	pls_auditoria_conta_grupo
					where	nr_Seq_analise  = nr_seq_analise_p
					and	nr_Seq_grupo 	= nr_Seq_grupo_atual_p
					and	coalesce(ie_pre_analise,'N') = 'N'
					and	coalesce(dt_liberacao::text, '') = '');
elsif (coalesce(nr_seq_analise_p,0) > 0) and (coalesce(nr_Seq_grupo_atual_p,0) = 0) then
	select	max(nr_seq_ordem)
	into STRICT	nr_seq_ordem_auditor_w
	from 	pls_auditoria_conta_grupo
	where	nr_seq_analise 	= nr_seq_analise_p
	and	nr_seq_ordem	= (	SELECT  max(nr_seq_ordem)
					from 	pls_auditoria_conta_grupo
					where	nr_Seq_analise  = nr_seq_analise_p
					and	coalesce(ie_pre_analise,'N') = 'N'
					and	(nm_auditor_atual IS NOT NULL AND nm_auditor_atual::text <> ''));

	if (coalesce(nr_seq_ordem_auditor_w,0) > 0) then
		select	max(nm_auditor_atual)
		into STRICT	nm_auditor_atual_w
		from	pls_auditoria_conta_grupo
		where	nr_seq_ordem = nr_seq_ordem_auditor_w
		and	nr_seq_analise	= nr_seq_analise_p
		and	(nm_auditor_atual IS NOT NULL AND nm_auditor_atual::text <> '');
	end if;

end if;

return	nm_auditor_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
