SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_auditor_rec_glosa ( nr_seq_conta_rec_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_rec_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_rec_p pls_conta_mat.nr_sequencia%type, nr_seq_motivo_glosa_p tiss_motivo_glosa.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
NA - Nome do auditor
GA - Nome do grupo auditor
SGA - Sequencial do grupo auditor
*/
nr_seq_fluxo_item_w	pls_analise_fluxo_item.nr_sequencia%type;
nm_auditor_w		pessoa_fisica.nm_pessoa_fisica%type;
ds_grupo_w		pls_grupo_auditor.nm_grupo_auditor%type;
ds_retorno_w		pls_grupo_auditor.nm_grupo_auditor%type;
nr_seq_conta_w		pls_conta.nr_Sequencia%type;
nr_seq_conta_proc_w	pls_conta_proc.nr_sequencia%type;
nr_seq_conta_mat_w	pls_conta_mat.nr_sequencia%type;
nr_seq_grupo_w		pls_grupo_auditor.nr_sequencia%type;


BEGIN

if (nr_seq_conta_rec_p IS NOT NULL AND nr_seq_conta_rec_p::text <> '') then

	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_rec_glosa_conta
	where	nr_sequencia = nr_seq_conta_rec_p;

	if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_fluxo_item_w
		from	pls_analise_fluxo_item_v
		where	nr_seq_conta = nr_seq_conta_w
		and	coalesce(nr_seq_conta_proc::text, '') = ''
		and	coalesce(nr_seq_conta_mat::text, '') = ''
		and	nr_seq_motivo_glosa = nr_seq_motivo_glosa_p
		and	vl_glosa > 0;

		if (coalesce(nr_seq_fluxo_item_w::text, '') = '') then
			select	max(nr_sequencia)
			into STRICT	nr_seq_fluxo_item_w
			from	pls_analise_fluxo_item_v
			where	nr_seq_conta = nr_seq_conta_w
			and	coalesce(nr_seq_conta_proc::text, '') = ''
			and	coalesce(nr_seq_conta_mat::text, '') = ''
			and	vl_glosa > 0;
		end if;
	end if;

elsif (nr_seq_conta_proc_rec_p IS NOT NULL AND nr_seq_conta_proc_rec_p::text <> '') then

	select	max(nr_seq_conta_proc)
	into STRICT	nr_seq_conta_proc_w
	from	pls_rec_glosa_proc
	where	nr_sequencia = nr_seq_conta_proc_rec_p;

	if (nr_seq_conta_proc_w IS NOT NULL AND nr_seq_conta_proc_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_fluxo_item_w
		from	pls_analise_fluxo_item_v
		where	nr_seq_conta_proc = nr_seq_conta_proc_w
		and	nr_seq_motivo_glosa = nr_seq_motivo_glosa_p
		and	vl_glosa > 0;

		if (coalesce(nr_seq_fluxo_item_w::text, '') = '') then
			select	max(nr_sequencia)
			into STRICT	nr_seq_fluxo_item_w
			from	pls_analise_fluxo_item_v
			where	nr_seq_conta_proc = nr_seq_conta_proc_w
			and	vl_glosa > 0;
		end if;
	end if;

elsif (nr_seq_conta_mat_rec_p IS NOT NULL AND nr_seq_conta_mat_rec_p::text <> '') then

	select	max(nr_seq_conta_mat)
	into STRICT	nr_seq_conta_mat_w
	from	pls_rec_glosa_mat
	where	nr_sequencia = nr_seq_conta_mat_rec_p;

	if (nr_seq_conta_mat_w IS NOT NULL AND nr_seq_conta_mat_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_fluxo_item_w
		from	pls_analise_fluxo_item_v
		where	nr_seq_conta_mat = nr_seq_conta_mat_w
		and	nr_seq_motivo_glosa = nr_seq_motivo_glosa_p
		and	vl_glosa > 0;

		if (coalesce(nr_seq_fluxo_item_w::text, '') = '') then
			select	max(nr_sequencia)
			into STRICT	nr_seq_fluxo_item_w
			from	pls_analise_fluxo_item_v
			where	nr_seq_conta_mat = nr_seq_conta_mat_w
			and	vl_glosa > 0;
		end if;
	end if;
end if;

if (nr_seq_fluxo_item_w IS NOT NULL AND nr_seq_fluxo_item_w::text <> '') then
	select	max(nm_auditor),
		max(ds_grupo),
		max(nr_seq_grupo)
	into STRICT	nm_auditor_w,
		ds_grupo_w,
		nr_seq_grupo_w
	from	pls_analise_fluxo_item_v
	where	nr_sequencia = nr_seq_fluxo_item_w;

	if (ie_opcao_p = 'NA') then
		ds_retorno_w := nm_auditor_w;
	elsif (ie_opcao_p = 'GA') then
		ds_retorno_w := ds_grupo_w;
	elsif (ie_opcao_p = 'SGA') then
		ds_retorno_w := nr_seq_grupo_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_auditor_rec_glosa ( nr_seq_conta_rec_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_rec_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_rec_p pls_conta_mat.nr_sequencia%type, nr_seq_motivo_glosa_p tiss_motivo_glosa.nr_sequencia%type, ie_opcao_p text) FROM PUBLIC;

