CREATE OR REPLACE FUNCTION pls_obter_audi_at_fluxo_html ( nr_seq_analise_p bigint, nr_Seq_grupo_atual_p bigint) RETURNS varchar AS $body$
DECLARE


nm_auditor_atual_w	varchar(100):= '';
/*Rotina que irá verificar no timer da função OPS- Análise de contas médicas, exclusiva para o HTML5, não pode ser colocada a 
data de finalização is null pois ao finalizar a análise é finalizada a análise porém o auditor ainda está no fluxo	*/
		

BEGIN

if ( coalesce(nr_seq_analise_p,0) > 0) and (coalesce(nr_Seq_grupo_atual_p,0)>0) then	
	
	select	max(nm_auditor_atual)
	into STRICT	nm_auditor_atual_w
	from 	pls_auditoria_conta_grupo
	where	nr_seq_analise 	= nr_seq_analise_p
	and	nr_Seq_grupo 	= nr_Seq_grupo_atual_p
	and	nr_seq_ordem	= (	SELECT  max(nr_seq_ordem)
					from 	pls_auditoria_conta_grupo
					where	nr_Seq_analise  = nr_seq_analise_p
					and	nr_Seq_grupo 	= nr_Seq_grupo_atual_p
					and	(dt_inicio_auditor IS NOT NULL AND dt_inicio_auditor::text <> '') );	
end if;

return	nm_auditor_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
