CREATE OR REPLACE FUNCTION pls_obter_aud_grupo_pendente ( nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000) := '0';
nr_seq_auditoria_w		bigint;
nr_seq_ordem_w			bigint;
nr_seq_grupo_ordem_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		a.nr_seq_ordem
	from	pls_auditoria		b,
		pls_auditoria_grupo	a
	where	b.nr_sequencia		= a.nr_seq_auditoria
	and	a.nr_seq_grupo		= nr_seq_grupo_p
	and	coalesce(a.dt_liberacao::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_auditoria_w,
	nr_seq_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	min(nr_seq_ordem)
	into STRICT	nr_seq_grupo_ordem_w
	from	pls_auditoria_grupo
	where	nr_seq_auditoria = nr_seq_auditoria_w
	and	coalesce(dt_liberacao::text, '') = '';

	if (nr_seq_grupo_ordem_w = nr_seq_ordem_w) then
		ds_retorno_w := ds_retorno_w || ',' || nr_seq_auditoria_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
