CREATE OR REPLACE FUNCTION pls_obter_ausencia_coop ( nr_seq_cooperado_p pls_cooperado.nr_sequencia%type, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			pls_motivo_afast_coop.ds_motivo%type := null;
nr_seq_coop_ausencia_w		pls_cooperado_ausencia.nr_sequencia%type;
dt_referencia_w			timestamp;


BEGIN
dt_referencia_w := trunc(dt_referencia_p);

select	max(a.nr_sequencia)
into STRICT	nr_seq_coop_ausencia_w
from	pls_motivo_afast_coop b,
	pls_cooperado_ausencia a
where	b.nr_sequencia		= a.nr_seq_motivo
and	a.nr_seq_cooperado	= nr_seq_cooperado_p
and	dt_referencia_w between trunc(dt_inicio) and fim_dia(coalesce(dt_fim,dt_referencia_w));

if (nr_seq_coop_ausencia_w IS NOT NULL AND nr_seq_coop_ausencia_w::text <> '') then
	select	max(b.ds_motivo)
	into STRICT	ds_retorno_w
	from	pls_motivo_afast_coop b,
		pls_cooperado_ausencia a
	where	b.nr_sequencia	= a.nr_seq_motivo
	and	a.nr_sequencia	= nr_seq_coop_ausencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
