CREATE OR REPLACE FUNCTION pls_obter_ausencia_operador ( nr_seq_ausencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_motivo_w			bigint;
ds_retorno_w			varchar(255);


BEGIN

select	max(nr_seq_motivo)
into STRICT	nr_seq_motivo_w
from	pls_atendimento_ausencia
where	nr_sequencia	= nr_seq_ausencia_p;

ds_retorno_w	:= substr(pls_obter_motivo_ausencia(nr_seq_motivo_w),1,255);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
