SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_benef_cpt ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

select	count(*)
into STRICT	qt_registros_w
from	pls_carencia	b,
	pls_segurado	a
where	b.nr_seq_segurado	= a.nr_sequencia
and	a.nr_sequencia		= nr_seq_segurado_p
and	b.ie_cpt		= 'S';

if (qt_registros_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
