CREATE OR REPLACE FUNCTION pls_obter_benef_perfil_simul ( nr_seq_simulacao_p pls_simulacao_preco.nr_sequencia%type, nr_seq_perfil_p pls_simulacao_perfil.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


qt_vidas_w	bigint;


BEGIN

if (nr_seq_simulacao_p IS NOT NULL AND nr_seq_simulacao_p::text <> '') then
	select	sum(a.qt_beneficiario)
	into STRICT	qt_vidas_w
	from	pls_simulpreco_coletivo a,
		pls_simulacao_perfil b
	where	b.nr_sequencia	= a.nr_seq_simul_perfil
	and	b.nr_seq_simulacao = nr_seq_simulacao_p;
elsif (nr_seq_perfil_p IS NOT NULL AND nr_seq_perfil_p::text <> '') then
	select	sum(qt_beneficiario)
	into STRICT	qt_vidas_w
	from	pls_simulpreco_coletivo
	where	nr_seq_simul_perfil = nr_seq_perfil_p;
end if;

return	coalesce(qt_vidas_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
